class SplitVec4Node extends ShaderNode {
  constructor() {
    super("SplitVec4");
    this.inputs = {
      vector: {
        default: "vec4f(0.0)"
      }
    };
    // This node has multiple outputs!
  }
  build(pin, __, ctx) {
    const conn = ctx.shaderGraph.getInput(this, "vector");
    const vec = conn ? ctx.resolve(conn.fromNode, conn.fromPin) : this.inputs.vector.default;

    // Store the temp once
    if (!this._temp) {
      this._temp = ctx.temp("vec4f", vec);
    }

    // Return different components based on which output pin is being resolved
    switch (pin) {
      case "x":
        return {
          out: `${this._temp}.x`,
          type: "f32"
        };
      case "y":
        return {
          out: `${this._temp}.y`,
          type: "f32"
        };
      case "z":
        return {
          out: `${this._temp}.z`,
          type: "f32"
        };
      case "w":
        return {
          out: `${this._temp}.w`,
          type: "f32"
        };
      default:
        return {
          out: this._temp,
          type: "vec4f"
        };
    }
  }
}
